import axios from 'axios';
import { SignInRequestDto, SignUpRequestDto } from './request/auth';
import { SignInResponseDto, SignUpResponseDto } from './response/auth';
import { ResponseDto } from './response';
import { error } from 'console';
import { GetSignInUserResponseDto } from './response/user';
import { PostBoardRequestDto } from './request/board';
import { postBoardResponseDto } from './response/board';

const DOMAIN = "http://localhost:4000";

const API_DOMAIN = `${DOMAIN}/api/v1`;

const authorization = (accessToken: string) => {
    return { headers: { Authorization: `Bearer ${accessToken}` } }
}

const SIGN_IN_URL = () => `${API_DOMAIN}/auth/sign-in`
const SIGN_UP_URL = () => `${API_DOMAIN}/auth/sign-up`

export const signInRequest = async(requestBody: SignInRequestDto) => {
    const result = await axios.post(SIGN_IN_URL(), requestBody)
        .then(response => {
            const responseBody: SignInResponseDto = response.data;
            return responseBody
        })
        .catch(error => {
            if(!error.response.data) return null;
            const responseBody: ResponseDto = error.response.data;
            console.error('Unexpected error:', error);
            return responseBody;
        })
    return result;
}

export const SignUpRequest = async(requestBody: SignUpRequestDto) => {
    const result = await axios.post(SIGN_UP_URL(), requestBody)
        .then(response =>{
            const responseBody: SignUpResponseDto = response.data;
            return responseBody
        })
        .catch(error => {
            if(!error.response.data) return null;
            const responseBody: ResponseDto = error.response.data;
            console.error('Unexpected error:', error);
            return responseBody;
        })
    return result;
}

const POAST_BOARD_URL = () => `${API_DOMAIN}/board`;

export const postBoardRequest = async(requestBody: PostBoardRequestDto, accessToken: string)=>{
    const result = await axios.post(POAST_BOARD_URL(), requestBody, authorization(accessToken))
        .then(response => {
            const responseBody: postBoardResponseDto = response.data;
            return responseBody;
        })
        .catch(error => {
            const responseBody: ResponseDto = error.response.data;
            return responseBody;
        })
    return result;
}


const GET_SIGN_IN_USER_URL = () => `${API_DOMAIN}/user`

export const getSignInUserRequest = async(accessToken: string) => {
    const result = await axios.get(GET_SIGN_IN_USER_URL(), authorization(accessToken))
        .then(response =>{
            const responseBody: GetSignInUserResponseDto = response.data;
            return responseBody;
        })
        .catch(error => {
            if(!error.response) return null;
            const responseBody: ResponseDto = error.response.data;
            return responseBody;
        })
    return result;
}

const FILE_DOMAIN = `${DOMAIN}/file`;

const FILE_UPLOAD_URL =() => `${FILE_DOMAIN}/upload`;

const multipartFormData = { headers: {'Content-Type': 'multpart/form-data'}};

export const fileUploadRequest = async(data: FormData) =>{
    const result = await axios.post(FILE_UPLOAD_URL(), data,multipartFormData)
        .then(response => {
            const responseBody:String = response.data;
            return responseBody;
        })
        .catch(error=>{
            return null;
        })
    return result;
}
