import{ ChangeEvent, Dispatch, SetStateAction, forwardRef } from 'react'
import './style.css';

//----------   interface : properties       ----------//
interface Props {
    lable : string;
    type: 'text' | 'password';
    placeholder: string;
    value : string;
    setValue: Dispatch<SetStateAction<string>>;
    error: boolean;

    icon?: string;
    onButtonClick?: () => void;

    message?: string;
}
//----------componet : FavoriteListItem----------//
const InputBox = forwardRef<HTMLInputElement, Props>((props: Props, ref) => {

//----------   state : properties       ----------//
    const{ lable, type, placeholder, value, error, icon, message } = props;
    const{ setValue, onButtonClick } = props;

//----------event handler: input값 변경 ----------//
    const onChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
        const { value } = event.target;
        setValue(value);
    }

//---------- render : FavoriteListItem---------- // 
    return(
        <div className='inputbox'>
            <div className='inputbox-label'>{ lable }</div>
            <div className={ error ? 'inputbox-container-error' : 'inputbox-container' }>
                <input type={ type } className='input' placeholder={ placeholder } value={ value } onChange={ onChangeHandler }/>
                {onButtonClick !== undefined && (
                <div className='icon-button'>
                    {icon !== undefined && (
                    <div className={`icon ${icon}`}></div>
                    )}
                </div>
                )}
            </div>
            {message !== undefined &&(
            <div className='inputbox-message'>{ message }</div>
            )}
        </div>
    )
});

export default InputBox;
