import{ forwardRef } from 'react'
import './style.css';

//----------   interface : properties       ----------//
interface Props {
    lable : string;
    type: 'text' | 'password';
    placeholder: string;
    value : string;
    error: boolean;
}
//----------componet : FavoriteListItem----------//
const InputBox = forwardRef<HTMLInputElement, Props>((props: Props, ref) => {

//----------   state : properties       ----------//
    const{ lable, type, placeholder, error } = props;

//---------- render : FavoriteListItem---------- // 
    return(
        <div className='inputbox'>
            <div className='inputbox-label'>{lable}</div>
            <div className={ error ? 'inputbox-container-error' : 'inputbox-container' }>
                <input type={ type } className='input' placeholder='placeholder'/>
                <div className='icon-button'>
                    <div className='icon eye-light-off-icon'></div>
                </div>
            </div>
            <div className='inputbox-message'>{  }</div>
        </div>
    )
});

export default InputBox;
