import React, { ChangeEvent, KeyboardEvent, useEffect, useRef, useState } from 'react'
import './style.css';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { AUTH_PATH, BOARD_DETAIL_PATH, BOARD_UPDATE_PATH, BOARD_WRITE_PATH, MAIN_PATH, SEARCH_PATH, USER_PATH } from 'constant';
import { useCookies } from 'react-cookie';
import { useBoardStore, useLoginUserStore } from 'stores';

//           component: Header          //
export default function Header() {

//           state: 로그인 유저 상태         //
const { loginUser, setLoginUser, resetLoginUser } = useLoginUserStore();

//           state: path 상태          //
const { pathname } = useLocation();

const isAuthPage = pathname.startsWith(AUTH_PATH());
const isMainPage = pathname.startsWith(MAIN_PATH());
const isSearchPage = pathname.startsWith(SEARCH_PATH(''));
const isBoardDetailPage = pathname.startsWith(BOARD_DETAIL_PATH(''));
const isBoardWritePage = pathname.startsWith(BOARD_WRITE_PATH());
const isBoardUpdatePage = pathname.startsWith(BOARD_UPDATE_PATH(''));
const isUserPage = pathname.startsWith(USER_PATH(''));

//           state: cookie 상태         //
const [cookies, setCookies] = useCookies();

//           state: 로그인 상태         //
const [isLogin, setLogin] = useState<boolean>(false);


//          function: navigate          //
const navigate = useNavigate();

//          event handler: 로고 클릭 이벤트 처리 함수          //
const onLogoClickHandler = () => {
  navigate(MAIN_PATH());
}

//          component: 검색 버튼          //
const SearchButton = () => {

//          state: 검색 버튼 요소 참조 상태         //
  const searchButtonRef = useRef<HTMLDivElement | null>(null);

//          state: 검색 버튼 상태         //
  const [status, setStatus] = useState<boolean>(false);

//          state: 검색어 상태         //
  const [word, setWord] = useState<string>('');

//          state: 검색어 path variable 상태        //
  const { searchWord } = useParams();

//          event handler: 검색어 변경           //
  const onSearchWordChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setWord(value);
  }

//          event handler: 검색 키 다운 이벤트          //
  const onSearchWordKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
    if(event.key !== 'Enter') return;
    if(!searchButtonRef.current) return;
    searchButtonRef.current.click();
  }

//          event handler: 검색 아이콘 클릭          //
  const onSearchButtonClickHandler = () => {
    if(!status){
      setStatus(!status);
      return;
    }
    navigate(SEARCH_PATH(word));
  }

//          effect: 검색어 path valrialve 변경시 실행         //
  useEffect(()=>{
    if(searchWord){
      setWord(searchWord);
      setStatus(true);
    }
  }, [searchWord]);

  if(!status)
//          render: 검색 버튼 (클릭 false)           //
    return(
      <div className='icon-button'>
        <div className='icon search-light-icon' onClick={onSearchButtonClickHandler}></div>
      </div>
    )
//          render: 검색 버튼 (클릭 true)           //
  return(
    <div className='header-search-input-box'>
      <input className='header-search-input' type='text' placeholder='검색어를 입력해주세요' value={word} onChange={onSearchWordChangeHandler} onKeyDown={onSearchWordKeyDownHandler}/>
      <div ref={searchButtonRef} className='icon-button' onClick={onSearchButtonClickHandler}>
        <div className='icon search-light-icon'></div>
      </div>
    </div>
  )
}

//          component: login mypage logout 버튼          //
  const LoginMyPageButton = () => {

//          state: userEmail path variable 상태          //
  const { userEmail } = useParams();

//          event handler: 마이페이지 버튼 클릭          //
  const onMyPageButtonClickHandler = () => {
    if(!loginUser) return;
    navigate(USER_PATH(''));
  }

//          event handler: 로그아웃 버튼 클릭          //
  const onSignOutButtonClickHandler = () => {
    if(!loginUser) return;
    const{ email } = loginUser
    navigate(USER_PATH(''));
  }

//          event handler: 로그인 버튼 클릭          //
  const onSignInButtonClickHandler = () => {
    navigate(MAIN_PATH());
  }

//           render: mypage          //
  if(isLogin && userEmail === loginUser?.email)
    return(
      <div className='white-button' onClick={onSignOutButtonClickHandler}>{'로그아웃'}</div>
    )

//           render: mypage          //
  if(isLogin)
     return(
      <div className='white-button' onClick={onMyPageButtonClickHandler}>{'마이페이지'}</div>
     )

//           render: login          //
     return(
      <div className='black-button' onClick={onSignInButtonClickHandler}>{'로그인'}</div>
     )
  }


//          component: upload 버튼           //
  const UploadButton = () => {

//          state: 게시물 상태           //
    const { title, content, boardImageFileList, resetBoard } = useBoardStore();

//           event handler: 업로드 버튼 클릭 이벤트          //
    const onUploadButtonClickHandler = () => {

    }

//          render: 업로드 버튼              //
    if (title && content)
      return(
        <div className='black-button' onClick={onUploadButtonClickHandler}>{'업로드'}</div>
      )

//          render: 업로드 불가  버튼          //
    return(
      <div className='disable-button'>{'업로드 불가'}</div>
    )
  }


//           render: Header          //
  return (
    <div id='header'>
      <div className='header-container'>
        <div className='header-left-box' onClick={onLogoClickHandler}>
          <div className='icon-box'>
            <div className='icon logo-dark-icon'></div>
          </div>
          <div className='header-logo'>{'여기는 뭐가들어갈까요'}</div>
        </div>
        <div className='header-right-box'>
          {(isAuthPage || isMainPage || isSearchPage || isBoardDetailPage) && <SearchButton/>}
          {(isMainPage || isSearchPage || isBoardDetailPage || isUserPage) && <LoginMyPageButton/>}
          {(isMainPage || isSearchPage || isBoardDetailPage || isUserPage) && <LoginMyPageButton/>}
          <UploadButton/>
        </div>
      </div>
    </div>
  )
}
