import React, { ChangeEvent, KeyboardEvent, useRef, useState } from 'react'
import './style.css'
import InputBox from 'components/InputBox';
import { signInRequest } from 'apis';
import { SignInRequestDto } from 'apis/request/auth';
import { SignInResponseDto } from 'apis/response/auth';
import { ResponseDto } from 'apis/response';
import { useCookies } from 'react-cookie';
import { MAIN_PATH } from 'constant';
import { useNavigate } from 'react-router-dom';

//          component: Authentication          //
export default function Authentication() {
  
//          state: 화면 상태         //
const [view, setView] = useState<'sign-in' | 'sign-up'>('sign-in');

//          function: navigate          //
const navigator = useNavigate();

//          state: cookie 상태         //
const [cookies, setCookie] = useCookies();

//          component: signincard          //
const SignInCard = () => {
  
//          state: email 요소 참조 상태         //
  const emailRef = useRef<HTMLInputElement | null>(null);
//          state: password 요소 참조 상태         //
  const passwordRef = useRef<HTMLInputElement | null>(null);
  

//          state: email 상태         //
  const [email, setEmail] = useState<string>('');
//          state: password 상태         //
  const [password, setPassword] = useState<string>('');
//          state: password type 상태         //
  const [passwordType, setPasswordType] = useState<'text'|'password'>('password');
//          state: password button icon 상태         //
  const [passwordButtonIcon, setPasswordButtonIcon] = useState<'eye-light-off-icon' | 'eye-light-on-icon' >('eye-light-off-icon');
//          state: error 상태         //
  const [error, setError] = useState<boolean>(false);

//          function: sign in response 처리 함수          //
  const signInResponse = (responseBody: SignInResponseDto | ResponseDto | null) => {
    if(!responseBody){
      alert('네트워크 이상입니다.');
      return;
    }
    const { code } = responseBody;
    if (code === 'DBE') alert('데이터베이스 오류입니다');
    if (code === 'VF' || code === 'SF') setError(true);
    if (code !== 'SU') return;
    const { token, expirationTime } = responseBody as SignInResponseDto;
    const now = new Date().getTime();
    const expires = new Date(now + expirationTime * 1000);

    setCookie('accessToken', token, { expires, path: MAIN_PATH() });
    navigator(MAIN_PATH());
    
  }

//          event handler: email 변경 change         //
  const onEmailChangeHandler = (event:ChangeEvent<HTMLInputElement>) => {
    setError(false);
    const { value } = event.target;
    setEmail(value);
  }

//          event handler: password 변경 change         //
const onPasswordChangeHandler = (event:ChangeEvent<HTMLInputElement>) => {
  setError(false);
  const { value } = event.target;
  setPassword(value);
}

//          event handler: login button click         //
  const onSignInButtonClickHandler = () => {
    const requestBody: SignInRequestDto = { email,password };
    signInRequest(requestBody).then(signInResponse);
  }

//          event handler: 회원가입 링크 click         //
  const onSignUpLinkClickHandler = () => {
    setView('sign-up')
  }

//          event handler: password button click         //
const onPasswordButtonClickHandler = () => {
  if (passwordType === 'text'){
    setPasswordType('password');
    setPasswordButtonIcon('eye-light-off-icon');
  } else {
    setPasswordType('text');
    setPasswordButtonIcon('eye-light-on-icon');
  }
}

//          event handler: email 인풋 키 다운         //
  const onEmailKeyDownHandler = (event : KeyboardEvent<HTMLInputElement>) => {
    if (event.key !== 'Enter') return;
    if (!passwordRef.current) return;
    passwordRef.current.focus();
  }

//          event handler: password 인풋 키 다운         //
  const onPasswordKeyDownHandler = (event : KeyboardEvent<HTMLInputElement>) => {
    if (event.key !== 'Enter') return;
  onSignInButtonClickHandler();
  }

//          render: signincard            //
    return(
      <div className='auth-card'>
        <div className='auth-card-box'>
          <div className='auth-card-top'>
            <div className='auth-card-title-box'>
              <div className='auth-card-title'>{'로그인'}</div>
            </div>
            <InputBox ref={emailRef} lable='이메일 주소' type='text' placeholder='이메일 주소를 입력해 주세요.' error={error}value={email} onChange={onEmailChangeHandler} onKeyDown={onEmailKeyDownHandler} />
            <InputBox ref={passwordRef} lable='패스워드' type={passwordType} placeholder='비밀번호를 입력해 주세요.' error={error}  value={password} onChange={onPasswordChangeHandler} icon={passwordButtonIcon} onButtonClick={onPasswordButtonClickHandler} onKeyDown={onPasswordKeyDownHandler} />
            
          </div>
          <div className='auth-card-bottom'>
            {error &&
            <div className='auth-sign-in-error-box'>
              <div className='auth-sign-in-error-message'>
                {'이메일 주소 또는 비밀번호를 잘못입력했습니다. \n입력하신 내용을 다시 확인해주세요.'}
                </div>
            </div>
            }
            <div className='black-large-full-button' onClick={onSignInButtonClickHandler}>{'로그인'}</div>
            <div className='auth-description'>{'신규 사용자이신가요? '}<span className='auth-description-link' onClick={onSignUpLinkClickHandler}>{'회원가입'}</span></div>
          </div>
        </div>
      </div>
    )
  }


//          component: signupcard          //
  const SignUpCard = () => {

//          state: email 요소 참조 상태          //
  const emailRef = useRef<HTMLInputElement | null>(null);
//          state: password 요소 참조 상태          //
  const passwordRef = useRef<HTMLInputElement | null>(null);
//          state: passwordCheck 요소 참조 상태          //
  const passwordCheckRef = useRef<HTMLInputElement | null>(null);

//          state: password 타입 상태          //
  const [passwordType, setPasswordType] = useState<'text' | 'password'>('password');
//          state: passwordCheck 타입 상태          //
  const [passwordCheckType, setPasswordCheckType] = useState<'text' | 'password'>('password');

//          state: 페이지 번호 상태          //
  const [page,setPage] = useState<1|2>(1);
//          state: 이메일 상태          //
  const [email, setEmail] = useState<string>('');
//          state: password 상태          //
  const [password, setPassword] = useState<string>('');
//          state: password 상태          //
  const [passwordCheck, setPasswordCheck] = useState<string>('');

//          state: email 에러 상태          //
  const [isEmailError, setEmailError] = useState<boolean>(false);
//          state: password 에러 상태          //
  const [isPasswordError, setPasswordError] = useState<boolean>(false);
//          state: passwordCheck 에러 상태          //
  const [isPasswordCheckError, setPasswordCheckError] = useState<boolean>(false);

//          state: email 에러 메세지 상태          //
  const [emailErrorMessage, setEmailErrorMessage] = useState<string>('');
//          state: password 에러 메세지 상태          //
  const [passwordErrorMessage, setPasswordErrorMessage] = useState<string>('');
//          state: passwordCheck 에러 메세지 상태          //
  const [passwordCheckErrorMessage, setPasswordCheckErrorMessage] = useState<string>('');

 //          state: 패스워드 버튼 아이콘 상태          //
  const [passwordButtonIcon, setPasswordButtonIcon] = useState<'eye-light-off-icon' | 'eye-light-on-icon' >('eye-light-off-icon');

 //          state: 패스워드 체크 버튼 아이콘 상태          //
  const [passwordCheckButtonIcon, setPasswordCheckButtonIcon] = useState<'eye-light-off-icon' | 'eye-light-on-icon' >('eye-light-off-icon');

//          event handler: 이메일 변경 이벤트           //
  const onEmailChangeHandler = (event: ChangeEvent<HTMLInputElement>) =>{
    const {value} = event.target;
    setEmail(value);
  }

//          event handler: 패스워드 변경 이벤트           //
  const onPasswordChangeHandler = (event: ChangeEvent<HTMLInputElement>) =>{
    const {value} = event.target;
    setPassword(value);
  }

//          event handler: 패스워드 변경 이벤트           //
  const onPasswordCheckChangeHandler = (event: ChangeEvent<HTMLInputElement>) =>{
  const {value} = event.target;
  setPasswordCheck(value);
}

//          event handler: 패스워드 버튼 클릭              //
  const onPasswordButtonClickHandler = () => {
    if(passwordButtonIcon==='eye-light-off-icon'){
      setPasswordButtonIcon('eye-light-on-icon');
      setPasswordType('text');
    } else {
      setPasswordButtonIcon('eye-light-off-icon');
      setPasswordType('password');
    }
  }

//          event handler: 패스워드 버튼 클릭              //
const onPasswordCheckButtonClickHandler = () => {
  if(passwordCheckButtonIcon==='eye-light-off-icon'){
    setPasswordCheckButtonIcon('eye-light-on-icon');
    setPasswordCheckType('text');
  } else {
    setPasswordCheckButtonIcon('eye-light-off-icon');
    setPasswordCheckType('password');
  }
}

//          event handler: 다음단계 버튼 클릭            //
  const onNextButtonClickHandler = () => {
    const emaiilPattern = /^$[a-zA-Z0-9]*@([-.]?[a-zA-Z0-9])*\.[a-zA-Z]{2/
    if(!email){
      setEmailError(true);
      setEmailErrorMessage('이메일 주소 포멧이 맞지 않습니다.')
    }
    setPage(2);
  }


//          event handler: 이메일 키 다운 이벤트            //
  const onEmailKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
    if(event.key !== 'Enter') return;
    if(!passwordRef.current) return;
    passwordRef.current.focus();
  }
//          event handler: 패스워드 키 다운 이벤트            //
  const onPasswordKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
    if(event.key !== 'Enter') return;
    if(!passwordCheckRef.current) return;
    passwordCheckRef.current.focus();
  }
//          event handler: 패스워드 체크 키 다운 이벤트            //
  const onPasswordCheckKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
    if(event.key !== 'Enter') return;
    if(!passwordRef.current) return;
    passwordRef.current.focus();
  }

//          render: signupcard            //
    return(
      <div className='auth-card'>
        <div className='auth-card-box'>
          <div className='auth-card-top'>
            <div className='auth-card-title-box'>
              <div className='auth-card-title'>{'회원가입'}</div>
              <div className='auth-card-page'>{`${page}/2`}</div>
            </div>
            <InputBox ref={emailRef} 
            type='text' placeholder='이메일 주소를 입력해 주세요.' value={email} onChange={onEmailChangeHandler} error={isEmailError} message={emailErrorMessage} lable='이메일 주소*' />
            <InputBox ref={passwordRef} type={passwordType} placeholder='비밀번호를 입력해 주세요' value={password} onChange={onPasswordChangeHandler} error={isPasswordError} message={passwordErrorMessage} lable='비밀번호*' icon={passwordButtonIcon} onButtonClick={onPasswordButtonClickHandler}/>
            <InputBox ref={passwordCheckRef} type={passwordCheckType} placeholder='비밀번호를 다시 입력해 주세요' value={passwordCheck} onChange={onPasswordCheckChangeHandler} error={isPasswordCheckError} message={passwordCheckErrorMessage} lable='비밀번호 확인*' icon={passwordCheckButtonIcon} onButtonClick={onPasswordCheckButtonClickHandler}/>
          </div>
          <div className='auth-card-bottom'>
            <div className='black-large-full-button'>{'다음단계'}</div>
          <div className='auth-description'>{'이미 계정이 있으신가요? '}<span className='auth-description-link' onClick={onNextButtonClickHandler}>{'로그인'}</span></div>
          </div>
        </div>
      </div>
    )
  }
    
//          render: Authentication            //
  return (
    <div id='auth-wrapper'>
      <div className='auth-container'>
        <div className='auth-jumbotron-box'>
          <div className='auth-jumbotron-contents'>
            <div className='auth-logo-icon'></div>
            <div className='auth-jumbotron-text-box'>
              <div className='auth-jumbotron-text'>{'환영합니다'}</div>
              <div className='auth-jumbotron-text'>{'이름이뭐가될까요?'}</div>
            </div>
          </div>
        </div>
        {view === 'sign-in' && <SignInCard/>}
        {view === 'sign-up' && <SignUpCard/>}
      </div>
    </div>
  )
}
